import React, { createContext, useContext, useState } from "react";

export const AuthContext = createContext()

function App() {

  const[isLoggedIn, setIsLoggedIn] = useState(true)

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  return (
    <div>
      <AuthContext.Provider value={{ isLoggedIn, handleLogin, handleLogout}}>
        <Navigation />
        <MainContent />
        <MyComponent />
        <ChangeStatusButton />
      </AuthContext.Provider>
    </div>
  );
}


function Navigation() {
  const { isLoggedIn } = useContext(AuthContext);


  return (
    <nav>
      <ul>
        {isLoggedIn ? (
          <li>
            <a href="/">dashboard</a>
          </li>
        ) : (
          <li>
            <a href="/login">Login</a>
          </li>
        )}
      </ul>
    </nav>
  );
}

function MyComponent() {
  const { isLoggedIn } = useContext(AuthContext);

  return <div>{isLoggedIn ? 'logged in' : "not logged in"}</div>
}


function ChangeStatusButton() {
  const { isLoggedIn, handleLogin, handleLogout } = useContext(AuthContext);

  const changeStatus = () => {
    if (isLoggedIn = true) {
      handleLogout()
    } else {
      handleLogin()
    }
  }
  return <button onClick={changeStatus}>Change Status</button>
}

function MainContent() {
  const { isLoggedIn } = useContext(AuthContext);

  return (
    <main>
      {isLoggedIn ? (
        <h1>Welcome to the dashboard!</h1>
      ) : (
        <h1>Please log in to access the dashboard.</h1>
      )}
    </main>
  );
}


